<!DOCTYPE html>
<html>
<head>
    <title>TERMINATOR ARCADE</title>
    <style>
        body { margin:0; background:#000; overflow:hidden; font-family:monospace; color:#0f0 }
        canvas { display:block; image-rendering:pixelated }
        #crt { position:fixed; top:0; left:0; width:100%; height:100%; 
               background:linear-gradient(rgba(0,255,0,0.1) 1px, transparent 1px);
               background-size:100% 3px; pointer-events:none; opacity:0.3 }
        #hud { position:fixed; bottom:10px; left:10px; color:red }
    </style>
</head>
<body>
    <canvas id="game"></canvas>
    <div id="crt"></div>
    <div id="hud">T-800 ONLINE</div>

    <script>
        // Game state
        const state = {
            player: { x:0, y:0, bullets:[], lastShot:0, lives:3 },
            enemies: [], enemyBullets: [], wave:0, score:0,
            keys: { left:false, right:false, shoot:false }
        };

        // Init canvas
        const canvas = document.getElementById('game');
        const ctx = canvas.getContext('2d');
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;

        // Game loop
        function update() {
            // Player movement
            if (state.keys.left) state.player.x -= 5;
            if (state.keys.right) state.player.x += 5;

            // Shooting
            if (state.keys.shoot && Date.now() - state.player.lastShot > 300) {
                state.player.bullets.push({ x:state.player.x, y:state.player.y });
                state.player.lastShot = Date.now();
            }

            // Update bullets
            state.player.bullets.forEach(b => b.y -= 7);
            state.enemyBullets.forEach(b => b.y += 5);

            // Spawn enemies
            if (state.enemies.length === 0) {
                state.wave++;
                for (let i=0; i<5 + state.wave; i++) {
                    state.enemies.push({
                        x: Math.random() * canvas.width,
                        y: Math.random() * -100 - 50,
                        speed: 1 + Math.random() * state.wave/2
                    });
                }
            }

            // Draw everything
            ctx.fillStyle = '#000';
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            // Draw player (T-800)
            ctx.fillStyle = '#f00';
            ctx.fillRect(state.player.x-15, state.player.y-15, 30, 30);

            // Draw bullets
            ctx.fillStyle = '#0f0';
            state.player.bullets.forEach(b => ctx.fillRect(b.x-2, b.y-10, 4, 20));

            // Draw enemies
            ctx.fillStyle = '#00f';
            state.enemies.forEach(e => ctx.fillRect(e.x-10, e.y-10, 20, 20));

            requestAnimationFrame(update);
        }

        // Input handlers
        window.addEventListener('keydown', e => {
            if (e.key === 'ArrowLeft') state.keys.left = true;
            if (e.key === 'ArrowRight') state.keys.right = true;
            if (e.key === ' ') state.keys.shoot = true;
        });
        window.addEventListener('keyup', e => {
            if (e.key === 'ArrowLeft') state.keys.left = false;
            if (e.key === 'ArrowRight') state.keys.right = false;
            if (e.key === ' ') state.keys.shoot = false;
        });

        // Init player position
        state.player.x = canvas.width/2;
        state.player.y = canvas.height - 50;

        // Start game
        update();
    </script>
</body>
</html>
